package constructors;

public class defaultValues {

	//Note - The default Value for the class willalways be null
	
	static String Namachivaya;
	int rollno;
	Integer AccBalance;
	char vaalga;
	double phonenumber;
	Character naathan;

	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		defaultValues example = new defaultValues();

		System.out.println("The default Value For String\t"+example.Namachivaya);
		System.out.println("The default Value For int\t"+example.rollno);
		System.out.println("The default Value For Integer\t"+example.AccBalance);
		System.out.println("The default Value For Char\t"+example.vaalga);
		System.out.println("The default Value For double\t"+example.phonenumber);
		System.out.println("The default Value for Character \t"+example.naathan);
		
		

	}

}
